mapper.select:
SELECT cno, cid, tid, term FROM studentms.ct


        cid = #{cid}


        AND tid = #{tid}


        AND term = #{term}


ORDER BY cno

SELECT cno, cid, tid, term FROM studentms.ct
WHERE tid = #{tid} AND term = #{term}
ORDER BY cno

SELECT ct.cno, c.cname, t.tid, t.tname, ct.day,ct.period
FROM studentms.c c
INNER JOIN studentms.ct ct ON c.cid = ct.cid
INNER JOIN studentms.t t ON ct.tid = t.tid


        t.tid = #{tid}



            AND t.tname = #{tname}


            AND t.tname LIKE CONCAT('%', #{tname}, '%')



        AND c.cid = #{cid}



            AND c.cname = #{cname}


            AND c.cname LIKE CONCAT('%', #{cname}, '%')



ORDER BY ct.cno

mapper.delete:
DELETE FROM studentms.ct


        cno = #{cno}



            cid = #{cid}


            AND tid = #{tid}


            AND term = #{term}

mapper.update:
UPDATE studentms.ct
SET cid = #{cid}, tid = #{tid}, term = #{term}
WHERE cno = #{cno}

UPDATE studentms.ct


        cid = #{cid},


        tid = #{tid},


        term = #{term}


WHERE cno = #{cno}

mapper.select:
SELECT cno, cid, tid, term FROM studentms.ct
WHERE term = #{term}
ORDER BY cno
